function run() {
gs.info("WF: Running replicated LUN to existing host workflow");
var id = "dc4895ea1be300103ecd4196bc4bcb1c";
var restApi = new VPLEX_VMAX_Abstract_RestApi(id);
var url = 'vco/api/workflows/3c311d89-4954-478e-b8d4-644ddcb9d79b/executions/';

	var vmax_name = current.variables.vmaxname;
	var srdf_group_number = current.variables.srdf_group_number;
	var vplex_name = current.variables.vplexname;
	var volume_names = current.variables.volume_list;
	//var volume_names = (current.variables.volume_list).replace(/,/g, "|");
	var volume_Size = current.variables.volume_size;
	//var volume_Size = (current.variables.volume_size).replace(/,/g, "|");
	var symmetrix_id = current.variables.symmetrix_id;
	var add_all_cluster_nodes = current.variables.add_all_cluster_nodes;
	var enter_remote_symmetrix_id = current.variables.enter_remote_symmetrix_id;
	
	var host_name = current.variables.host_name;
	var allcluster = false;
  if("No"!=add_all_cluster_nodes)
		{
			allcluster = true;
		}
 
var jsonTry =  new global.JSON();

	
var parameters = '{"parameters":[{"value":{"string":{"value":"'+host_name+'"}},"type":"string","name":"hostName","scope":"local"},{"value":{"string":{"value":"'+volume_names+'"}},"type":"string","name":"volumeNames","scope":"local"},{"value":{"string":{"value":"'+volume_Size+'"}},"type":"string","name":"volumeSizes_GB","scope":"local"},{"value":{"string":{"value":"'+symmetrix_id+'"}},"type":"string","name":"symm","scope":"local"},{"value":{"string":{"value":"'+enter_remote_symmetrix_id+'"}},"type":"string","name":"remoteSymm","scope":"local"},{"value":{"string":{"value":"'+srdf_group_number+'"}},"type":"string","name":"srdfGroupNumber","scope":"local"},{"value":{"string":{"value":"'+allcluster+'"}},"type":"boolean","name":"addToAllClusterNodes","scope":"local"}]}';
gs.info("PayLoad Parmeters to vRO=>"+parameters);
var payload = jsonTry.decode(parameters);
var rest = restApi.RESTCall(url, "post", payload, false);
gs.info(rest.responseBody);

	
	
	
	
	
//var payload = jsonTry.decode(payLo);
//var rest = restApi.RESTCall(url, "post", payload, false);
workflow.scratchpad.responseValue = rest.response;
workflow.scratchpad.statusCodeValue = rest.statusCode;
workflow.scratchpad.locationValue = rest.location;
workflow.scratchpad.error = rest.error;
gs.info(rest.statusCode + " ::: " + rest.response + " ::: " + rest.error);
	
}

