activity.result = run();

function run() {
gs.info("WF: Running Remove VPLEX Instance workflow - callback to vro");
var id = "dc4895ea1be300103ecd4196bc4bcb1c";
var util = VPLEX_VMAX_Abstract_RestApi.get(id);
var response = workflow.scratchpad.responseValue;
var rest = util._returVroResult(response);
gs.info("Please print:  " + rest.statusCode);
var responseVal = rest.response;
var statusCode = rest.statusCode;
var errorValue = rest.error;
workflow.scratchpad.statusCode = statusCode;
gs.info("Value:: " + statusCode);
if (statusCode === 0) {
	gs.info("Callback to vro has been completed Successfully");
	var gReco = new GlideRecord('x_440435_vplex_vma_vplex_adapters');
	var query = 'vplex_adapters=' + current.variables.adapter_name;
	gReco.addQuery(query);
	gReco.query();
	while(gReco.next()) {
		var sysIdVal = gReco.sys_id;
		if(gReco.get(sysIdVal))
			gReco.deleteRecord();
	}
	var responseBody = responseVal.getBody();
	workflow.scratchpad.output = responseBody['output-parameters'];
} else {
	gs.info("Callback to vro failed");
	workflow.scratchpad.error = errorValue;
}
}

