function run() {
	var id = "dc4895ea1be300103ecd4196bc4bcb1c";
	var restApi = new VPLEX_VMAX_Abstract_RestApi(id);
	var url = 'vco/api/workflows/3a9346fd-50e6-4af1-99f6-3fee323132da/executions/';
	var adapter_name = current.variables.adapter_name;
	var jsonTry =  new global.JSON();
	var parameters = '{"parameters":[{"value":{"string":{"value": "'+ adapter_name+'"}},"type":"string","name":"name","scope":"local"}]}';
	var payload = jsonTry.decode(parameters);
	var rest = restApi.RESTCall(url, "post", payload,false);
	gs.info(rest.getResponseBody);
	workflow.scratchpad.responseValue = rest.response;
    workflow.scratchpad.statusCodeValue = rest.statusCode;
	workflow.scratchpad.error = rest.error;
	gs.info(rest.statusCode + " ::: " + rest.response + " ::: " + rest.error);                                               
}

